INSERT INTO `article` VALUES ('118', '代码测试二', 'admin', '<pre>\n<code class=\'html\'>\n  <!DOCTYPE html>\n<html lang=\"en\">\n<head>\n    <meta charset=\"UTF-8\">\n    <title>邮件发送成功</title>\n    <link rel=\"stylesheet\" href=\"/css/reset.css\">\n    <style>\n        html {\n            min-height: 100%;\n            _height:100%;\n            height:100%;\n        }\n        body {\n            min-height: 100%;\n            _height:100%;\n            height:100%;\n            background-image: url(/image/bgimg_top.jpg);\n        }\n        .tip {\n            height: 100%;\n            width: 100%;\n            display: table-cell;\n            vertical-align: middle;\n            text-align: center;\n        }\n        .tip span {\n            display: inline-block;\n        }\n    </style>\n</head>\n<body>\n<script type=\"text/javascript\" src=\"/js/jquery.min.js\"></script>\n<script type=\"text/javascript\" src=\"/js/plugin/layer/layer.js\"></script>\n<script type=\"text/javascript\" src=\"/js/comm.js\"></script>\n<script>\n   var layerIndex = comm.layer.dialog({\n                       type: 1,\n                       title: \'\',\n                       content: \'<div class=\"tip\"><span><%=locals.data%></span><div>\',\n                       closeBtn: 0,\n                       area: [\'30%\',\'30%\']\n\n                    });\n   $(window).resize(function(){\n       var $layer = $(\'.layui-layer\');\n       $(\'.tip\').height($layer.height()).width($layer.width()).css({\n           padding: \'0 10px 0\'\n       });\n   }).trigger(\'resize\');\n</script>\n</body>\n</html>\n</code>\n</pre>', '2017-08-04 14:10:44', '', '15', '', '0', '0', '3', null, '1');
