.last.title=&quot;&quot;&gt;<!--%=--></a>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;%} else=&quot;&quot;&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;没有了\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;%}%&gt;<!--%}%--><!--%}--><!--%if(sibling.last!=null){%--></li><li><p>下一篇：</p>&lt;%if(sibling.next!=null){%&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"/article-detail/<%=sibling.next.id%>.html\"> &lt;%= sibling.next.title=&quot;&quot;&gt;<!--%=--></a>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;%} else=&quot;&quot;&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span href=\"javascript:void(0);\">没有了</span>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;%}%&gt;<!--%}%--><!--%}--><!--%if(sibling.next!=null){%--></li></ul><p><!--文章相关标签 end-->\n\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<!--相关文章 begin--><!--相关文章 end--></p><h4 class=\"comment-theme\">评论(<span id=\"counts\"></span>)</h4><p><!--评论回复部分 begin--><!--评论回复部分 end-->&lt;%&gt;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<!--发表评论 begin--></p><h3>发表评论</h3><p><!--action=\"/comment/<%=data[0][\'id\']%>.html\"--></p><form id=\"commentForm\" data-commentid=\"&lt;%=data[0][&#39;id&#39;]%&gt;\" method=\"post\"><p><textarea autocomplete=\"off\" name=\"content\" tabindex=\"4\" cols=\"100%\" rows=\"6\"></textarea></p><p class=\"clearfix\"><input name=\"submit\" value=\"发表评论\" class=\"declare-btn\" type=\"submit\" id=\"declareComment\"/></p></form><!--发表评论 end-->\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;%} else=&quot;&quot;&gt;<p class=\"need-login\">您还未<a target=\"_blank\" href=\"/login?show=1\" class=\"nologin js_nologin_btn\">登录</a>，暂不能评论……</p>&lt;%}%&gt;<!--%}%--><!--%}--><!--%--><p></p></article><p></p><p></p><aside><section><h2>最新文章</h2><ul class=\"number-wrap list-paddingleft-2\"><li><p>&lt;%\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var i = 0,\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;length = lastest.length;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for(;i &lt; length; i++){\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;%&gt;</p></li><li><p><em class=\"number\">&lt;%=(i+1)%&gt;<!--%=(i+1)%--></em><a href=\"/article-detail/<%=lastest[i].id%>.html\" class=\"tooltip\" title=\"&lt;%=lastest[i].title%&gt;\">&lt;%=lastest[i].title%&gt;<!--%=lastest[i].title%--></a></p></li><li>&lt;%}%&gt;</li><!--%}%--></ul></section><section><h2>热点文章</h2><ul class=\"number-wrap list-paddingleft-2\"><li><p>&lt;%\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var j = 0,\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;length = topsix.length;\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for(j; j &lt; length; j++) {\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;%&gt;</p></li><li><p><em class=\"number ls3\">&lt;%= j=&quot;&quot;&gt;<!--%=--></em> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a class=\"tooltip\" href=\"/article-detail/<%= topsix[j].id %>.html\" title=\"&lt;%=topsix[j].title%&gt;\">&lt;%=topsix[j].title%&gt;<!--%=topsix[j].title%--></a></p></li><li>&lt;%}%&gt;</li><!--%}%--></ul></section><section><h2>热评文章</h2></section></aside><p></p></main>&lt;% include=&quot;&quot; footer.ejs=&quot;&quot;&gt;<script type=\"text/javascript\" src=\"/js/plugin/highlight/highlight.pack.js\"></script><script type=\"text/template\" id=\"template\"><div class=\"declare-comment-wrap js_reply_wrap hidden\"><h3>发表回复</h3><form><p><textarea name=\"comment\" tabindex=\"4\" cols=\"100%\" rows=\"6\"></textarea></p><p class=\"clearfix\"><input name=\"submit\" value=\"回复\" class=\"declare-btn\" type=\"submit\" onsubmit=\"return false;\"></p></form></div></script><script type=\"text/template\" id=\"mostcomment\"><ul class=\"number-wrap\"><$\n        var j = 0,\n                length = mostcomment.length;\n        for(j; j < length; j++) {\n        $><li class=\"ellipsis\"><i class=\"number ls3\"><$= (j + 1) $></i>            <a class=\"tooltip\" href=\"/article-detail/<$= mostcomment[j].id $>.html\" title=\"共有<$=mostcomment[j].counts$>条评论\"><$=mostcomment[j].title$></a></li><$}$></ul></script><script type=\"text/template\" id=\"commentsReplays\"><!--评论和回复 begin--><div class=\"comments-wrap\"><ul class=\"comments-all\"><$var m=0,\n            commentsReply = data,\n            l = commentsReply.length;\n\n            for(;m < l; m++){\n            $><li class=\"js_comment_single\"><!--展示评论 begin--><div class=\"comments\"><div class=\"user\"><a href=\"javascript:void(0);\">                            <img src=\"/image/user.jpg\" />                        </a></div><div class=\"comment-body js_comment_body\"><div class=\"comment-name\"><a href=\"javascript:void(0);\" class=\"js_comment_name\">\n                                <$=commentsReply[m].username$>                            </a></div><p class=\"comment-content\"><$=commentsReply[m].content$></p><div class=\"comment-footer js_comment_footer\"><time><$=moment(commentsReply[m].comm_time).format(\'YYYY年MM月DD HH:mm\')$></time><a href=\"javascript:void(0);\" data-flag=\"1\" class=\"js_reply\" data-replytype=\"4\" data-replyid=\"<$=commentsReply[m].commid$>\" data-replyuser=\"<$=commentsReply[m].from_uid$>\" data-dicid=\"<$=commentsReply[m].dicid$>\" >                                <i title=\"回复图标\" class=\"iconfont icon-huifu\"></i>回复                            </a></div></div></div><!--展示评论 end-->\n                <$\n                loop(commentsReply[m].replys);\n                function loop(replys){\n\n                if(!replys||replys.length < 1){return;}\n                if(replys.length > 0){\n                for(var k = 0; k < replys.length;k++){\n                var reply =replys[k];\n                if(reply.hasOwnProperty(\'rpid\')){\n                $><ul class=\"comment-childs\"><li id=\"<$=reply.rpid$>\"><div class=\"comments\"><div class=\"user\"><a href=\"javascript:void(0);\">                                    <img src=\"/image/user.jpg\" />                                </a></div><div class=\"comment-body js_comment_body\"><div class=\"comment-name\"><a href=\"javascript:void(0);\">\n                                        <$=reply.from_username$> <span class=\"reply_text\">回复</span> <$=reply.to_username$>                                    </a></div><p class=\"comment-content\"><$=reply.reply_content$></p><div class=\"comment-footer js_comment_footer\"><time><$=moment(replys[k].reply_time).format(\'YYYY年MM月DD HH:mm\')$></time><a href=\"javascript:void(0);\" class=\"js_reply\" data-flag=\"1\" data-replytype=\"5\" data-commid=\"<$=reply.comment_id$>\" data-replyid=\"<$=reply.rpid$>\" data-replyuser=\"<$=reply.rp_from_uid$>\" data-dicid=\"<$=reply.dicid$>\">                                        <i title=\"回复图标\" class=\"iconfont icon-huifu\"></i>回复                                    </a></div></div></div></li></ul><$}$>\n                <$\n                if(reply.replys){\n                loop(reply.replys);\n                }\n                $>\n                <$ }$>\n\n                <$}$>\n                <$}$>\n                <!--表示针对评论的回复--></li><$}$></ul></div><!--评论和回复 end--></script><script type=\"text/template\" id=\"page\"><div class=\"pages clearfix\"><input type=\"hidden\" value=\"<$=pages.allPageNum$>\" id=\"messageCounts\"><ul class=\"page-wrap\"><$\n            //每页显示几条数据\n            var  showPageNum = parseInt(pages.showPageNum),\n            //总页数\n            totalPage = Math.ceil(pages.allPageNum/showPageNum),\n            //当前页\n            currentPage = parseInt(pages.currentPage);\n            if(totalPage > 1){\n            $><li class=\"page\"><span><$= currentPage $>/<$= totalPage $></span></li><$ if(currentPage!== 1){ $><li class=\"page\" data-pagenum=\"<$= currentPage - 1 $>\">                <a href=\"javascript:void(0);\">\n                    <                </a></li><$ } $>\n\n            <$\n            for(var i = 0;i < totalPage ;i++){\n            //循环中的当前页码\n            var loopPage = i + 1;\n            $><li class=\"page\" data-pagenum=\"<$=loopPage$>\">                <a  <$ if(loopPage === currentPage) { $>\n                class=\"active\" href=\"javascript:void(0);\"\n                <$ } else { $>\n                href=\"javascript:void(0);\"\n                <$ } $>\n                >\n                <$= loopPage $>                </a></li><$}$>\n\n            <$if(currentPage !== totalPage){$><li class=\"page\" data-pagenum=\"<$= currentPage + 1 $>\">                <a href=\"javascript:void(0);\">\n                    >                </a></li><li class=\"page\" data-pagenum=\"<$=totalPage$>\">                <a href=\"javascript:void(0);\">尾页</a></li><$}$>\n            <$}$></ul></div></script><script type=\"text/javascript\" src=\"/js/plugin/moment.js\"></script><script>var articleId = <%=data[0][\'id\']%>;\n    //使用分页组件查询评论回复\n    function queryCommentReplay() {\n        comm.page.init({\n            url: \'/querycomments\',\n            container: \'pages\',\n            pageTmplId: \'page\',\n            dataTmplId: \'commentsReplays\',\n            dataId: \'commentsWrapper\',\n            postData: {\n                topicId: parseInt(articleId),\n                dicId: 1,\n                currentPage: 1,\n                showPageNum: 10\n            }\n        },function(){\n            $(\'#counts\').text($(\'#messageCounts\').val());\n        });\n    }\n\n    hljs.initHighlightingOnLoad();\n    window._bd_share_config={\n        \"common\":{\n            \"bdSnsKey\":{},\n            \"bdText\":\"<%=data[0][\'title\']%>\",\n            \"bdMini\":\"2\",\n            \"bdPic\":\"\",\n            \"bdStyle\":\"0\",\n            \"bdSize\":\"24\"\n        },\n        \"share\":{},\n        \"selectShare\":{\n            \"bdContainerClass\":null,\n            \"bdSelectMiniList\":[\"weixin\",\"qzone\",\"tsina\",\"tqq\",\"sqq\"]\n        }\n    };\n    with(document)0[(getElementsByTagName(\'head\')[0]||body).appendChild(createElement(\'script\')).src=\'http://bdimg.share.baidu.com/static/api/js/share.js?v=89860593.js?cdnversion=\'+~(-new Date()/36e5)];\n    $(init);\n    function init(){\n\n        //查询当前文章下的评论回复\n        queryCommentReplay();\n\n        //发表评论\n        $(\'#declareComment\').on(\'click\',function(e){\n            var $target = $(this);\n            e.preventDefault();\n            var $commentForm = $target.closest(\'form\'),\n                content =$commentForm.find(\'textarea\').val();\n            handleDeclareComment(content);\n        });\n\n        //回复\n        $(\'#commentsWrapper\').on(\'click\',\'.js_reply\',function(){\n            /*\n             获取html模板,根据reply_type是否为4来判断是不是根评论id(comment_id),\n             被回复评论的id(reply_id)，回复内容(reply_content)，评论人id(from_uid),\n             被评论人id(to_uid),评论主题id(dicid，文章是1)\n             */\n\n            /*\n             对于根评论来说：\n             comment_id 和 reply_id是一样的(所以只需要找到comment_id就行)；\n             被评论人id(就是评论表的from_id)\n             */\n            var $target = $(this),\n                flag = parseInt($target.data(\'flag\'));\n            if(flag){\n                //这里是显示回复框\n                showReplyArea($target);\n            } else {\n                //移除回复框\n                revmoeReplyArea($target);\n            }\n            /*\n             *   需求分析：\n             *       点击回复/取消回复:\n             *           判断是回复(1)、还是取消回复(0)\n             *\n             *           如果是回复：\n             *               显示回复框，将数据绑定到当前的回复框里；显示\"取消回复\"\n             *\n             *           如果是取消回复：\n             *           直接移除这个框就好了;显示\"回复\"\n             *\n             * */\n        });\n\n        //请求评论数最多的数据\n        function getMostComment() {\n            comm.ajax.commAjax({\n                url: \'/getmostcomment\',\n                successFn: function(data){\n                    if(data){\n                        comm.tmpl.render(\'mostcomment\',{mostcomment: data},\'mostCommentWrap\');\n                        $(\'#mostCommentWrap .tooltip\').toolTip();\n                    }\n                },\n                errorFn: function(err){\n                    if(err) comm.layer.alert(err);\n                }\n            })\n        }\n        getMostComment();\n    }\n\n    //临时保存数据到指定的dom对象\n    /**\n     *\n     * @param $target 回复按钮jquery 对象\n     * @param $store 存储回复信息的jquery对象\n     */\n    function saveDataOnDom($target,$store) {\n\n           //回复的是评论还是回复\n            rpType = $target.data(\'replytype\'),\n\n            //被回复的评论(回复)id\n            rpId = $target.data(\'replyid\'),\n\n            //被回复人id\n            rpUserId = $target.data(\'replyuser\'),\n\n            //哪个类型的回复(可能是文章的回复，也可能是说说的回复)\n            rpDicId = $target.data(\'dicid\'),\n\n            //根评论的id\n            commentId = $target.data(\'commid\')|null;\n\n        //如果回复的是评论\n        if(rpType===4){\n\n            commentId = rpId;\n        }\n\n        $store.data(\'reply\',{\n            commid: commentId,\n            rptype: rpType,\n            rpid: rpId,\n            rpUserId: rpUserId,\n            rpDicId: rpDicId,\n\n        });\n    }\n\n    //显示评论框\n    function showReplyArea($target) {\n\n        var templ = $(\'#template\').html(),\n            $templ = $(templ);\n\n        if($target.closest(\'.js_comment_footer\').find(\'.js_reply_wrap\').length < 1){\n            $target.closest(\'.js_comment_body\').append($templ);\n            $templ.find(\'input[type=\"submit\"]\').click(function(e){\n\n                var $target = $(this);\n                e.preventDefault();\n                handleReplySubmit($target);\n            });\n            saveDataOnDom($target,$templ);\n        }\n        $templ.slideDown();\n        $target.text(\'取消回复\').data(\'flag\',0);\n    }\n\n    //移除评论框\n    function revmoeReplyArea($target) {\n        $target.closest(\'.js_comment_body\').find(\'.js_reply_wrap\').slideUp();\n        $target.text(\'回复\').data(\'flag\',1);\n    }\n\n    //发表评论的逻辑处理\n    function handleDeclareComment(content){\n        comm.ajax.commAjax({\n            url: \'/comment\',\n            type: \'post\',\n            data: {\n                dicid: 1,\n                commentid: articleId,\n                content: content\n            },\n            successFn: function(data){\n                if(data.status === 1){\n                    comm.layer.msg(\'评论成功！\');\n                    queryCommentReplay();\n                    comm.scrollToElement(\'commentsWrapper\',-$(\'.js_head_wrap\').height());\n                } else {\n                    comm.layer.alert(data.des);\n                }\n            }\n        });\n\n        $(\'#commentForm\').find(\'textarea\').val(\'\');\n\n\n    }\n\n    //发表回复的逻辑处理\n    function handleReplySubmit($target){\n        var $replyDialog = $target.closest(\'.js_reply_wrap\'),\n            content = $target.closest(\'form\').find(\'textarea\').eq(0).val(),\n            replyData = $replyDialog.data(\'reply\'),\n            commid = replyData.commid,\n            rpid = replyData.rpid,\n            touid = replyData.rpUserId,\n            rptype = replyData.rptype,\n            dicid = replyData.rpDicId;\n        declareReply(commid,rpid,rptype,content,touid,dicid,$target);\n    }\n\n    //发表回复的逻辑处理\n    function declareReply(commid,rpid,rptype,content,touid,dicid,$target) {\n\n        comm.ajax.commAjax({\n            url: \'/reply\',\n            type: \'post\',\n            data: {\n                commid: commid,\n                replyid: rpid,\n                replytype: rptype,\n                content: content,\n                touid: touid,\n                dicid: dicid\n            },\n            errorFn: function(error){\n                comm.layer.alert(\'回复失败！\');\n            },\n            successFn: function(data){\n                if(data.data){\n                    comm.layer.alert(data.data.des,null,function(index){\n                        var $wrapper = $target.closest(\'.js_comment_body\'),\n                            $comments = $target.closest(\'.js_reply_wrap \');\n                        $wrapper.find(\'.js_reply\').eq(0).trigger(\'click\');\n                        queryCommentReplay();\n                        layer.close(index);\n                    });\n                }\n            }\n        });\n    }\n\n\n\n    function handleClick() {\n        var $target = $(this),\n            articleId = $target.data(\'articleId\');\n        ding($target,articleId);\n    }\n    var newHandleClick = comm.debounce(handleClick,500);\n\n    //点赞、踩\n    $(\"#likeorhate\").on(\'click\',\'.js_ding\',newHandleClick);\n\n    //顶的业务逻辑\n    function ding($self,articleId) {\n        comm.ajax.commAjax({\n            url: \'/ding\',\n            type: \'post\',\n            data: {id: articleId},\n            errorFn: function(error){\n                comm.layer.msg(\'顶操作失败！\');\n            },\n            successFn: function(data){\n                if(data.dm){\n                    $self.find(\'.js_ding_text\').text(data.dm[0].ding);\n                    $self.plusOne();\n                }\n            }\n        });\n    }\n\n    function handleCaiClick() {\n        var $target = $(this),\n            articleId = $target.data(\'articleId\');\n        cai($target,articleId);\n    }\n    var newHandleCaiClick = comm.debounce(handleCaiClick,500);\n    $(\"#likeorhate\").on(\'click\',\'.js_cai\',newHandleCaiClick);\n\n\n    //踩的业务逻辑\n    function cai($self,articleId) {\n        comm.ajax.commAjax({\n            url: \'/cai\',\n            type: \'post\',\n            data: {id: articleId},\n            errorFn: function(error){\n                comm.layer.msg(\'踩失败！\');\n            },\n            successFn: function(data){\n                if(data.c){\n                    $self.find(\'.js_cai_text\').text(data.c[0].cai);\n                    $self.plusOne();\n                }\n            }\n        });\n    }</script><!--%--><!--%--></code></p>', '2017-08-04 19:06:02', '', '8', '\n    <% include=\"\" header.ejs=\"\">首页><%=data[0].typename%>><%=data[0][\'title\']%><%=data[0][\'title\']%><%=data[0].author%>发布时间:<%=locals.dateformat(data[0].time, yyyy-mm-dd=\"\"><%= .views=\"\">次浏览<%-locals....', '0', '0', '3', null, '1');
